import React, { useState } from 'react';

export default function Upload({ onNext }) {
  const [docType, setDocType] = useState('');
  const [files, setFiles] = useState([]);

  const handleFiles = (e) => {
    setFiles([...files, ...Array.from(e.target.files)]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setFiles([...files, ...Array.from(e.dataTransfer.files)]);
  };

  const removeFile = (index) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  return (
    <div className="container">
      <h2>üìÑ Upload Identity Documents</h2>
      <p>Upload your Aadhaar, PAN, Passport, or other ID documents</p>

      {/* Document type */}
      <div className="form-group">
        <label>Document Type *</label>
        <select value={docType} onChange={(e) => setDocType(e.target.value)}>
          <option value="">Select document type...</option>
          <option value="Aadhaar">Aadhaar Card</option>
          <option value="PAN">PAN Card</option>
          <option value="Passport">Passport</option>
          <option value="Driving License">Driving License</option>
          <option value="Voter ID">Voter ID</option>
          <option value="Other">Other</option>
        </select>
      </div>

      {/* Upload zone */}
      <div
        className="upload-zone"
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
      >
        <input
          type="file"
          multiple
          accept=".pdf,.jpg,.jpeg,.png"
          onChange={handleFiles}
          style={{ display: 'block', width: '100%', height: '100%', opacity: 0, position: 'absolute', top: 0, left: 0, cursor: 'pointer' }}
        />
        <div className="upload-content">
          <div className="upload-icon">‚¨ÜÔ∏è</div>
          <div>Drop files here or click to upload</div>
          <small>Supports PDF, JPG, PNG (Max 10MB)</small>
        </div>
      </div>

      {/* Files preview */}
      {files.length > 0 && (
        <div className="files">
          {files.map((f, i) => (
            <div key={i} className="file">
              <div className="file-info">
                <span>{f.name}</span>
              </div>
              <button onClick={() => removeFile(i)}>‚úï</button>
            </div>
          ))}
        </div>
      )}

      {/* Continue button */}
      <div className="buttons">
        <button
          className="btn btn-primary"
          onClick={() => onNext({ files, documentType: docType })}
          disabled={!docType || files.length === 0}
        >
          Continue ‚Üí
        </button>
      </div>
    </div>
  );
}
