import React, { useState, useRef } from 'react';

export default function Upload({ onNext }) {
  const [docType, setDocType] = useState('');
  const [files, setFiles] = useState([]);
  const fileInputRef = useRef(null);

  const handleButtonClick = () => {
    console.log('Button clicked, triggering file input');
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    alert('Files selected!');
    if (e.target.files && e.target.files.length > 0) {
      const newFiles = Array.from(e.target.files);
      setFiles([...files, ...newFiles]);
    }
  };

  return (
    <div className="container">
      <h2>üìÑ Upload Identity Documents</h2>
      <p>Upload your Aadhaar, PAN, Passport, or other ID documents to extract information</p>

      <div className="form-group">
        <label>Document Type *</label>
        <select value={docType} onChange={(e) => setDocType(e.target.value)}>
          <option value="">Select document type...</option>
          <option value="Aadhaar">üÜî Aadhaar Card</option>
          <option value="PAN">üí≥ PAN Card</option>
          <option value="Passport">üõÇ Passport</option>
          <option value="Driving License">üöó Driving License</option>
          <option value="Voter ID">üó≥Ô∏è Voter ID</option>
          <option value="Other">üìã Other ID</option>
        </select>
      </div>

      <input 
        ref={fileInputRef}
        type="file" 
        multiple 
        accept=".pdf,.jpg,.jpeg,.png" 
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />

      <button
        onClick={handleButtonClick}
        style={{
          width: '100%',
          padding: '40px',
          fontSize: '20px',
          background: 'linear-gradient(135deg, #6366f1 0%, #8b5cf6 100%)',
          color: 'white',
          border: 'none',
          borderRadius: '12px',
          cursor: 'pointer',
          fontWeight: 'bold',
          marginBottom: '20px'
        }}
      >
        üì§ CLICK HERE TO UPLOAD FILES
      </button>

      {files.length > 0 && (
        <div style={{ marginTop: '2rem' }}>
          <h3>üìÇ Files: {files.length}</h3>
          {files.map((f, i) => (
            <div key={i} className="file">
              <div className="file-icon">üìÑ</div>
              <div className="file-info">
                <div className="file-name">{f.name}</div>
                <div className="file-type">{docType || 'No type'} ‚Ä¢ {(f.size / 1024).toFixed(1)} KB</div>
              </div>
              <button onClick={() => setFiles(files.filter((_, idx) => idx !== i))}>‚úï</button>
            </div>
          ))}
        </div>
      )}

      <div className="buttons">
        <button 
          className="btn btn-primary" 
          onClick={() => onNext({ files, documentType: docType })} 
          disabled={!docType || files.length === 0}
        >
          Continue to Extract ‚Üí
        </button>
      </div>
    </div>
  );
}
