import React, { useState } from 'react';

export default function Upload({ onNext }) {
  const [docType, setDocType] = useState('');
  const [files, setFiles] = useState([]);

  return (
    <div className="container">
      <h2>üìÑ Upload Identity Documents</h2>
      <p>Upload your Aadhaar, PAN, Passport, or other ID documents to extract information</p>

      <div className="form-group">
        <label>Document Type *</label>
        <select value={docType} onChange={(e) => setDocType(e.target.value)}>
          <option value="">Select document type...</option>
          <option value="Aadhaar">üÜî Aadhaar Card</option>
          <option value="PAN">üí≥ PAN Card</option>
          <option value="Passport">üõÇ Passport</option>
          <option value="Driving License">üöó Driving License</option>
          <option value="Voter ID">üó≥Ô∏è Voter ID</option>
          <option value="Other">üìã Other ID</option>
        </select>
      </div>

      <div style={{ 
        border: '3px solid red',
        padding: '20px',
        background: 'yellow',
        position: 'relative',
        zIndex: 9999
      }}>
        <h3>FILE INPUT BELOW (should be visible):</h3>
        <input 
          type="file" 
          multiple 
          accept=".pdf,.jpg,.jpeg,.png" 
          onChange={(e) => {
            alert('FILE SELECTED!');
            const newFiles = Array.from(e.target.files);
            setFiles([...files, ...newFiles]);
          }}
          style={{
            width: '100%',
            padding: '20px',
            fontSize: '18px',
            border: '5px solid blue',
            background: 'white',
            cursor: 'pointer',
            position: 'relative',
            zIndex: 10000
          }}
        />
      </div>

      <div style={{ marginTop: '20px', border: '2px solid green', padding: '20px' }}>
        <h3>OR TRY THIS LABEL METHOD:</h3>
        <input 
          type="file" 
          id="file-test"
          multiple 
          accept=".pdf,.jpg,.jpeg,.png" 
          onChange={(e) => {
            alert('LABEL METHOD WORKED!');
            const newFiles = Array.from(e.target.files);
            setFiles([...files, ...newFiles]);
          }}
          style={{ display: 'none' }}
        />
        <label 
          htmlFor="file-test"
          style={{
            display: 'block',
            padding: '20px',
            background: 'blue',
            color: 'white',
            cursor: 'pointer',
            fontSize: '18px',
            textAlign: 'center',
            border: '3px solid black'
          }}
        >
          CLICK HERE TO SELECT FILES
        </label>
      </div>

      {files.length > 0 && (
        <div style={{ marginTop: '2rem', border: '2px solid purple', padding: '20px' }}>
          <h3>üìÇ Files: {files.length}</h3>
          {files.map((f, i) => (
            <div key={i} style={{ padding: '10px', border: '1px solid #ccc', margin: '5px' }}>
              {f.name} - {(f.size / 1024).toFixed(1)} KB
            </div>
          ))}
        </div>
      )}

      <div className="buttons">
        <button 
          className="btn btn-primary" 
          onClick={() => onNext({ files, documentType: docType })} 
          disabled={!docType || files.length === 0}
        >
          Continue to Extract ‚Üí
        </button>
      </div>
    </div>
  );
}
