import React, { useState } from 'react';

export default function Upload({ onNext }) {
  const [docType, setDocType] = useState('');
  const [files, setFiles] = useState([]);

  const handleFiles = (e) => {
    const selected = Array.from(e.target.files);
    setFiles((prev) => [...prev, ...selected]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dropped = Array.from(e.dataTransfer.files);
    setFiles((prev) => [...prev, ...dropped]);
  };

  const removeFile = (index) => {
    setFiles(files.filter((_, i) => i !== index));
  };

  return (
    <div className="container">
      <h2>üìÑ Upload Identity Documents</h2>
      <p>Upload your Aadhaar, PAN, Passport, or other ID documents</p>

      {/* Document type */}
      <div className="form-group">
        <label>Document Type *</label>
        <select value={docType} onChange={(e) => setDocType(e.target.value)}>
          <option value="">Select document type...</option>
          <option value="Aadhaar">üÜî Aadhaar Card</option>
          <option value="PAN">üí≥ PAN Card</option>
          <option value="Passport">üõÇ Passport</option>
          <option value="Driving License">üöó Driving License</option>
          <option value="Voter ID">üó≥Ô∏è Voter ID</option>
          <option value="Other">üìã Other ID</option>
        </select>
      </div>

      {/* Upload Zone */}
      <label
        className="upload-zone"
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
      >
        <input
          type="file"
          multiple
          accept=".pdf,.jpg,.jpeg,.png"
          onChange={handleFiles}
          hidden
        />
        <div className="upload-icon">‚¨ÜÔ∏è</div>
        <div style={{ fontSize: '1.1rem', fontWeight: 600, color: '#374151', marginBottom: '0.5rem' }}>
          Drop files here or click to upload
        </div>
        <small style={{ color: '#9ca3af' }}>
          Supports PDF, JPG, PNG (Max 10MB per file)
        </small>
      </label>

      {/* Files preview */}
      {files.length > 0 && (
        <div className="files">
          <h3>üìÇ Uploaded Documents ({files.length})</h3>
          {files.map((f, i) => (
            <div key={i} className="file">
              <div className="file-icon">üìÑ</div>
              <div className="file-info">
                <div className="file-name">{f.name}</div>
                <div className="file-type">{docType} ‚Ä¢ {(f.size / 1024).toFixed(1)} KB</div>
              </div>
              <button
                onClick={() => removeFile(i)}
                className="remove-btn"
              >
                ‚úï
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Continue button */}
      <div className="buttons">
        <button
          className="btn btn-primary"
          onClick={() => onNext({ files, documentType: docType })}
          disabled={!docType || files.length === 0}
        >
          Continue to Extract ‚Üí
        </button>
      </div>
    </div>
  );
}
